// frontend/src/components/drivers/DriverList.js
import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '../ui/card';
import { Button } from '../ui/button';
import { driversAPI } from '../../services/api';
import { PlusIcon } from 'lucide-react';
import DriverForm from './DriverForm';

export default function DriverList() {
    const [drivers, setDrivers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showAddForm, setShowAddForm] = useState(false);

    useEffect(() => {
        loadDrivers();
    }, []);

    const loadDrivers = async () => {
        try {
            const response = await driversAPI.getAll();
            setDrivers(response.data);
        } catch (error) {
            console.error('Error loading drivers:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="space-y-6 p-6">
            <div className="flex justify-between items-center">
                <h2 className="text-3xl font-bold">Șoferi</h2>
                <Button onClick={() => setShowAddForm(true)}>
                    <PlusIcon className="w-4 h-4 mr-2" />
                    Adaugă Șofer
                </Button>
            </div>

            {showAddForm && (
                <Card className="mb-6">
                    <CardHeader>
                        <CardTitle>Adaugă Șofer Nou</CardTitle>
                    </CardHeader>
                    <CardContent>
                        <DriverForm 
                            onSubmit={async (data) => {
                                await driversAPI.create(data);
                                setShowAddForm(false);
                                loadDrivers();
                            }}
                            onCancel={() => setShowAddForm(false)}
                        />
                    </CardContent>
                </Card>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {drivers.map(driver => (
                    <DriverCard 
                        key={driver._id} 
                        driver={driver}
                        onUpdate={loadDrivers}
                    />
                ))}
            </div>
        </div>
    );
}

function DriverCard({ driver, onUpdate }) {
    return (
        <Card>
            <CardContent className="p-6">
                <div className="flex justify-between items-start">
                    <div>
                        <h3 className="font-medium text-lg">{driver.nume}</h3>
                        <p className="text-sm text-muted-foreground">
                            {driver.vehiculAsignat?.numarInmatriculare || 'Neasignat'}
                        </p>
                        <p className="text-sm text-muted-foreground mt-1">
                            CNP: {driver.cnp}
                        </p>
                    </div>
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                        driver.status === 'Activ' ? 'bg-green-100 text-green-800' :
                        driver.status === 'Concediu' ? 'bg-yellow-100 text-yellow-800' :
                        driver.status === 'Suspendat' ? 'bg-red-100 text-red-800' :
                        'bg-gray-100 text-gray-800'
                    }`}>
                        {driver.status}
                    </span>
                </div>

                <div className="mt-4 pt-4 border-t flex gap-2">
                    <Button variant="outline" size="sm">
                        Detalii
                    </Button>
                    <Button variant="outline" size="sm">
                        Editează
                    </Button>
                </div>
            </CardContent>
        </Card>
    );
}
