import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
    Dialog, 
    DialogContent, 
    DialogHeader, 
    DialogTitle 
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { 
    Select, 
    SelectContent, 
    SelectItem, 
    SelectTrigger, 
    SelectValue 
} from '@/components/ui/select';
import { 
    Form, 
    FormControl, 
    FormField, 
    FormItem, 
    FormLabel, 
    FormMessage 
} from '@/components/ui/form';
import { useForm } from 'react-hook-form';

const AddHolidayModal = ({ isOpen, onClose, onSuccess }) => {
    const [drivers, setDrivers] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    const form = useForm({
        defaultValues: {
            sofer: '',
            tipConcediu: '',
            dataStart: '',
            dataFinal: '',
            status: 'In Tara',
            motiv: '',
            observatii: ''
        }
    });

    useEffect(() => {
        const fetchDrivers = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/drivers`);
                setDrivers(response.data);
            } catch (error) {
                console.error('Eroare la încărcarea șoferilor:', error);
            }
        };

        fetchDrivers();
    }, []);

    const onSubmit = async (data) => {
        setIsLoading(true);
        try {
            await axios.post(`${process.env.REACT_APP_API_URL}/api/holidays`, {
                ...data,
                dataStart: new Date(data.dataStart),
                dataFinal: new Date(data.dataFinal)
            });
            onSuccess();
        } catch (error) {
            console.error('Eroare la salvarea concediului:', error);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <Dialog open={isOpen} onOpenChange={onClose}>
            <DialogContent className="sm:max-w-[425px]">
                <DialogHeader>
                    <DialogTitle>Adaugă Concediu Nou</DialogTitle>
                </DialogHeader>
                <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                        <FormField
                            control={form.control}
                            name="sofer"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Șofer</FormLabel>
                                    <Select 
                                        onValueChange={field.onChange} 
                                        defaultValue={field.value}
                                    >
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Selectează șofer" />
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent>
                                            {drivers.map((driver) => (
                                                <SelectItem 
                                                    key={driver._id} 
                                                    value={driver._id}
                                                >
                                                    {driver.nume}
                                                </SelectItem>
                                            ))}
                                        </SelectContent>
                                    </Select>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="tipConcediu"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Tip Concediu</FormLabel>
                                    <Select 
                                        onValueChange={field.onChange} 
                                        defaultValue={field.value}
                                    >
                                        <FormControl>
                                            <SelectTrigger>
                                                <SelectValue placeholder="Selectează tip" />
                                            </SelectTrigger>
                                        </FormControl>
                                        <SelectContent>
                                            {['Intre Curse', 'Medical', 'Neplatit', 'Special'].map((tip) => (
                                                <SelectItem key={tip} value={tip}>
                                                    {tip}
                                                </SelectItem>
                                            ))}
                                        </SelectContent>
                                    </Select>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="dataStart"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Data Start</FormLabel>
                                    <FormControl>
                                        <Input type="date" {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="dataFinal"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Data Final</FormLabel>
                                    <FormControl>
                                        <Input type="date" {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="motiv"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Motiv</FormLabel>
                                    <FormControl>
                                        <Input {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <FormField
                            control={form.control}
                            name="observatii"
                            render={({ field }) => (
                                <FormItem>
                                    <FormLabel>Observații</FormLabel>
                                    <FormControl>
                                        <Input {...field} />
                                    </FormControl>
                                    <FormMessage />
                                </FormItem>
                            )}
                        />

                        <div className="flex justify-end space-x-2">
                            <Button 
                                variant="outline" 
                                onClick={onClose}
                                type="button"
                            >
                                Anulează
                            </Button>
                            <Button 
                                type="submit" 
                                disabled={isLoading}
                            >
                                {isLoading ? 'Se salvează...' : 'Salvează'}
                            </Button>
                        </div>
                    </form>
                </Form>
            </DialogContent>
        </Dialog>
    );
};

export default AddHolidayModal;
