import React, { useState, useEffect } from 'react';
import { 
    Card, 
    CardHeader, 
    CardTitle, 
    CardContent 
} from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { 
    Table, 
    TableBody, 
    TableCell, 
    TableHead, 
    TableHeader, 
    TableRow 
} from '@/components/ui/table';
import axios from 'axios';
import { format } from 'date-fns';
import { Plus } from 'lucide-react';
import AddHolidayModal from './AddHolidayModal';

const Holidays = () => {
    const [holidays, setHolidays] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isAddModalOpen, setIsAddModalOpen] = useState(false);

    const fetchHolidays = async () => {
        try {
            const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/holidays`);
            setHolidays(response.data);
            setIsLoading(false);
        } catch (err) {
            setError('Nu s-au putut încărca concediile');
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchHolidays();
    }, []);

    const getStatusColor = (status) => {
        const statusColors = {
            'Activ': 'text-green-600 bg-green-100',
            'Plecat': 'text-blue-600 bg-blue-100',
            'In Tara': 'text-yellow-600 bg-yellow-100',
            'Concediu': 'text-purple-600 bg-purple-100',
            'Suspendat': 'text-red-600 bg-red-100',
            'Inactiv': 'text-gray-600 bg-gray-100'
        };
        return statusColors[status] || 'text-gray-600 bg-gray-100';
    };

    const handleAddSuccess = () => {
        fetchHolidays();
        setIsAddModalOpen(false);
    };

    if (isLoading) {
        return <div>Se încarcă...</div>;
    }

    if (error) {
        return <div>Eroare: {error}</div>;
    }

    return (
        <div className="p-6">
            <Card>
                <CardHeader className="flex flex-row items-center justify-between">
                    <CardTitle>Concedii</CardTitle>
                    <Button onClick={() => setIsAddModalOpen(true)}>
                        <Plus className="mr-2 h-4 w-4" />
                        Adaugă Concediu
                    </Button>
                </CardHeader>
                <CardContent>
                    <Table>
                        <TableHeader>
                            <TableRow>
                                <TableHead>Șofer</TableHead>
                                <TableHead>Tip Concediu</TableHead>
                                <TableHead>Data Start</TableHead>
                                <TableHead>Data Final</TableHead>
                                <TableHead>Zile Totale</TableHead>
                                <TableHead>Status</TableHead>
                                <TableHead>Acțiuni</TableHead>
                            </TableRow>
                        </TableHeader>
                        <TableBody>
                            {holidays.map((holiday) => (
                                <TableRow key={holiday._id}>
                                    <TableCell>{holiday.sofer.nume}</TableCell>
                                    <TableCell>{holiday.tipConcediu}</TableCell>
                                    <TableCell>
                                        {format(new Date(holiday.dataStart), 'dd.MM.yyyy')}
                                    </TableCell>
                                    <TableCell>
                                        {format(new Date(holiday.dataFinal), 'dd.MM.yyyy')}
                                    </TableCell>
                                    <TableCell>{holiday.zileTotale}</TableCell>
                                    <TableCell>
                                        <span className={`px-2 py-1 rounded-full text-sm ${getStatusColor(holiday.status)}`}>
                                            {holiday.status}
                                        </span>
                                    </TableCell>
                                    <TableCell>
                                        <Button variant="ghost" size="sm">
                                            Detalii
                                        </Button>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </CardContent>
            </Card>

            {isAddModalOpen && (
                <AddHolidayModal 
                    isOpen={isAddModalOpen}
                    onClose={() => setIsAddModalOpen(false)}
                    onSuccess={handleAddSuccess}
                />
            )}
        </div>
    );
};

export default Holidays;
