// C:\Users\Administrator\OneDrive\Documente\GitHub\salarizare\modern-backend\frontend\src\components\dashboard\ExpiringDocuments.js
import React, { useState, useEffect } from 'react'
import { dashboardApi } from '../../services/api/dashboard'
import { formatDate } from '../../utils/dateUtils'

export function ExpiringDocuments() {
  const [documents, setDocuments] = useState([])
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    loadDocuments()
  }, [])

  const loadDocuments = async () => {
    try {
      const response = await dashboardApi.getExpiringDocuments()
      setDocuments(response.data)
    } catch (error) {
      console.error('Error loading documents:', error)
    } finally {
      setLoading(false)
    }
  }

  if (loading) {
    return <div className="animate-pulse space-y-4">
      {[1,2,3].map(i => (
        <div key={i} className="h-16 bg-gray-200 rounded"></div>
      ))}
    </div>
  }

  return (
    <div className="space-y-4">
      {documents.map((doc) => (
        <div key={doc.id} className="flex justify-between items-center p-4 rounded-lg bg-gray-50">
          <div>
            <p className="text-sm font-medium">{doc.name}</p>
            <p className="text-xs text-gray-500">{doc.type}</p>
          </div>
          <p className={`text-sm font-medium ${
            new Date(doc.expiryDate) < new Date() 
              ? 'text-red-600' 
              : 'text-yellow-600'
          }`}>
            {formatDate(doc.expiryDate)}
          </p>
        </div>
      ))}
    </div>
  )
}
