import axios from 'axios';

const api = axios.create({
    baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',
    timeout: 10000,
    headers: {
        'Content-Type': 'application/json'
    }
});

// Request interceptor
api.interceptors.request.use(
    config => {
        const token = localStorage.getItem('token');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    error => {
        console.error('Request error:', error);
        return Promise.reject(error);
    }
);

// Response interceptor
api.interceptors.response.use(
    response => response,
    error => {
        if (error.response?.status === 401) {
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            if (window.location.pathname !== '/login') {
                window.location = '/login';
            }
        }
        return Promise.reject(error);
    }
);

// API methods
const authAPI = {
    login: (credentials) => api.post('/auth/login', credentials),
    getCurrentUser: () => api.get('/auth/me'),
};

const driversAPI = {
    getAll: () => api.get('/drivers'),
    getById: (id) => api.get(`/drivers/${id}`),
    create: (data) => api.post('/drivers', data),
    update: (id, data) => api.patch(`/drivers/${id}`, data),
    delete: (id) => api.delete(`/drivers/${id}`),
    getPerioade: (id, params) => api.get(`/drivers/${id}/perioade`, { params }),
    getSalariu: (id, params) => api.get(`/drivers/${id}/salariu`, { params })
};

const vehiclesAPI = {
    getAll: () => api.get('/vehicles'),
    getById: (id) => api.get(`/vehicles/${id}`),
    create: (data) => api.post('/vehicles', data),
    update: (id, data) => api.patch(`/vehicles/${id}`, data),
    delete: (id) => api.delete(`/vehicles/${id}`)
};

const dashboardAPI = {
    getStats: () => api.get('/dashboard/stats'),
    getActivity: () => api.get('/dashboard/activity'),
    getExpiringDocuments: () => api.get('/dashboard/documents/expiring'),
    getDriverStatus: () => api.get('/dashboard/drivers/status')
};

const salariesAPI = {
    getAll: (filters) => api.get('/salaries', { params: filters }),
    getByDriver: (soferId) => api.get(`/salaries/sofer/${soferId}`),
    create: (data) => api.post('/salaries', data),
    update: (id, data) => api.patch(`/salaries/${id}`, data),
    addBonus: (id, data) => api.post(`/salaries/${id}/bonus`, data),
    addDeducere: (id, data) => api.post(`/salaries/${id}/deducere`, data),
    calculate: (id) => api.post(`/salaries/${id}/calculeaza`)
};

const finesAPI = {
    getAll: (filters) => api.get('/fines', { params: filters }),
    getByDriver: (soferId) => api.get(`/fines/sofer/${soferId}`),
    create: (data) => api.post('/fines', data),
    update: (id, data) => api.patch(`/fines/${id}`, data),
    addPayment: (id, data) => api.post(`/fines/${id}/plati`, data),
    addContestation: (id, data) => api.post(`/fines/${id}/contestatie`, data)
};

export { 
    api as default, 
    authAPI, 
    driversAPI, 
    vehiclesAPI, 
    dashboardAPI,
    salariesAPI,
    finesAPI 
};
