// C:\Users\Administrator\OneDrive\Documente\GitHub\salarizare\modern-backend\frontend\src\components\drivers\DriverList.js
import React, { useState, useEffect } from 'react';
import DataTable from '../shared/tables/DataTable';
import Button from '../shared/buttons/Button';
import api from '../../services/api';
import { formatDate } from '../../utils/dateUtils';

const DriverList = () => {
    const [drivers, setDrivers] = useState([]);
    const [loading, setLoading] = useState(true);

    const columns = [
        { key: 'nume', label: 'Nume' },
        { key: 'cnp', label: 'CNP' },
        { 
            key: 'status', 
            label: 'Status',
            render: (status) => (
                <span className={`px-2 py-1 rounded-full text-xs ${
                    status === 'Activ' ? 'bg-green-100 text-green-800' :
                    status === 'Plecat' ? 'bg-blue-100 text-blue-800' :
                    status === 'In Tara' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-gray-100 text-gray-800'
                }`}>
                    {status}
                </span>
            )
        },
        { 
            key: 'dataAngajare', 
            label: 'Data Angajării',
            render: (date) => formatDate(date)
        },
        {
            key: 'zileLunaCurenta',
            label: 'Zile Luna Curentă',
            render: (value) => value || 0
        },
        {
            key: 'diurnaLunaCurenta',
            label: 'Diurnă Luna Curentă',
            render: (value) => `${value || 0} EUR`
        }
    ];

    useEffect(() => {
        loadDrivers();
    }, []);

    const loadDrivers = async () => {
        try {
            const response = await api.get('/drivers');
            setDrivers(response.data);
        } catch (error) {
            console.error('Error loading drivers:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="space-y-4">
            <div className="flex justify-between items-center">
                <h2 className="text-2xl font-bold">Șoferi</h2>
                <Button
                    variant="primary"
                    onClick={() => {/* handle add */}}
                >
                    Adaugă Șofer
                </Button>
            </div>

            <DataTable
                data={drivers}
                columns={columns}
                loading={loading}
                actions={(driver) => (
                    <div className="flex space-x-2">
                        <Button
                            size="sm"
                            variant="secondary"
                            onClick={() => {/* handle edit */}}
                        >
                            Editează
                        </Button>
                        <Button
                            size="sm"
                            variant={driver.status === 'Plecat' ? 'success' : 'primary'}
                            onClick={() => {/* handle status change */}}
                        >
                            {driver.status === 'Plecat' ? 'Marcare Sosit' : 'Marcare Plecat'}
                        </Button>
                    </div>
                )}
            />
        </div>
    );
};

export default DriverList;
