// C:\Users\Administrator\OneDrive\Documente\GitHub\salarizare\modern-backend\frontend\src\components\dashboard\ExpiredDocuments.js
import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import { formatDate } from '../../utils/dateUtils';

const ExpiredDocuments = () => {
   const [documents, setDocuments] = useState([]);

   useEffect(() => {
       loadDocuments();
   }, []);

   const loadDocuments = async () => {
       const response = await api.get('/documents/expiring');
       setDocuments(response.data);
   };

   return (
       <div className="bg-white rounded-lg shadow">
           <div className="p-4 border-b">
               <h2 className="text-lg font-medium">Documente care Expiră</h2>
           </div>
           <div className="p-4">
               <div className="space-y-4">
                   {documents.map(doc => (
                       <div
                           key={doc._id}
                           className="flex justify-between items-center border-b pb-2"
                       >
                           <div>
                               <p className="font-medium">{doc.tip}</p>
                               <p className="text-sm text-gray-500">
                                   {doc.sofer?.nume || doc.vehicul?.nume}
                               </p>
                           </div>
                           <div className="text-right">
                               <p className="text-sm">Expiră la:</p>
                               <p className={`text-sm ${
                                   new Date(doc.dataExpirare) < new Date()
                                       ? 'text-red-600'
                                       : 'text-yellow-600'
                               }`}>
                                   {formatDate(doc.dataExpirare)}
                               </p>
                           </div>
                       </div>
                   ))}
               </div>
           </div>
       </div>
   );
};

export default ExpiredDocuments;
