// C:\Users\Administrator\OneDrive\Documente\GitHub\salarizare\modern-backend\frontend\src\components\drivers\DriverList.js

import React, { useState, useEffect } from 'react';
import DataTable from '../shared/tables/DataTable';
import Button from '../shared/buttons/Button';
import api from '../../services/api';
import { formatDate } from '../../utils/dateUtils';
import DriverFormModal from './DriverFormModal';
import DemisieModal from './DemisieModal';

const DriverList = () => {
    const [drivers, setDrivers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectedDriver, setSelectedDriver] = useState(null);
    const [isDemisieModalOpen, setIsDemisieModalOpen] = useState(false);
    const [driverForDemisie, setDriverForDemisie] = useState(null);

    const columns = [
        { key: 'nume', label: 'Nume' },
        { key: 'cnp', label: 'CNP' },
        { 
            key: 'status', 
            label: 'Status',
            render: (status) => (
                <span className={`px-2 py-1 rounded-full text-xs ${
                    status === 'Activ' ? 'bg-green-100 text-green-800' :
                    status === 'Plecat' ? 'bg-blue-100 text-blue-800' :
                    status === 'In Tara' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-gray-100 text-gray-800'
                }`}>
                    {status}
                </span>
            )
        },
        { 
            key: 'dataAngajare', 
            label: 'Data Angajării',
            render: (date) => formatDate(date)
        },
        {
            key: 'zileLunaCurenta',
            label: 'Zile Luna Curentă',
            render: (value) => value || 0
        },
        {
            key: 'diurnaLunaCurenta',
            label: 'Diurnă Luna Curentă',
            render: (value) => `${value || 0} EUR`
        }
    ];

    useEffect(() => {
        loadDrivers();
    }, []);

    const loadDrivers = async () => {
        try {
            setLoading(true);
            const response = await api.get('/drivers', {
                params: {
                    activ: true // Doar șoferii activi
                }
            });
            setDrivers(response.data);
        } catch (error) {
            console.error('Error loading drivers:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleStatusChange = async (driver) => {
        try {
            const newStatus = driver.status === 'Plecat' ? 'In Tara' : 'Plecat';
            await api.patch(`/drivers/${driver._id}`, { 
                status: newStatus,
                dataUltimaSosire: newStatus === 'In Tara' ? new Date().toISOString() : undefined
            });
            await loadDrivers();
        } catch (error) {
            console.error('Error updating driver status:', error);
        }
    };

    const handleDemisie = (driver) => {
        setDriverForDemisie(driver);
        setIsDemisieModalOpen(true);
    };

    const handleDemisieSubmit = async (formData) => {
        try {
            await api.patch(`/drivers/${driverForDemisie._id}`, {
                status: 'Inactiv',
                activ: false,
                dataIncheiereContract: formData.dataDemisie,
                motivDemisie: formData.motiv,
                dataUltimaSosire: new Date().toISOString()
            });
            
            setIsDemisieModalOpen(false);
            setDriverForDemisie(null);
            await loadDrivers();
        } catch (error) {
            console.error('Error processing demisie:', error);
        }
    };

    const handleAddDriver = () => {
        setSelectedDriver(null);
        setIsModalOpen(true);
    };

    const handleEditDriver = (driver) => {
        setSelectedDriver(driver);
        setIsModalOpen(true);
    };

    const handleSubmit = async (formData) => {
        try {
            if (selectedDriver) {
                // Editare șofer existent
                await api.patch(`/drivers/${selectedDriver._id}`, formData);
            } else {
                // Adăugare șofer nou
                await api.post('/drivers', formData);
            }
            await loadDrivers();
            setIsModalOpen(false);
        } catch (error) {
            console.error('Error saving driver:', error);
        }
    };

    return (
        <div className="space-y-4">
            <div className="flex justify-between items-center">
                <h2 className="text-2xl font-bold">Șoferi</h2>
                <Button
                    variant="primary"
                    onClick={handleAddDriver}
                >
                    Adaugă Șofer
                </Button>
            </div>

            <DataTable
                data={drivers}
                columns={columns}
                loading={loading}
                actions={(driver) => (
                    <div className="flex space-x-2">
                        <Button
                            size="sm"
                            variant="secondary"
                            onClick={() => handleEditDriver(driver)}
                        >
                            Editează
                        </Button>
                        <Button
                            size="sm"
                            variant={driver.status === 'Plecat' ? 'success' : 'primary'}
                            onClick={() => handleStatusChange(driver)}
                        >
                            {driver.status === 'Plecat' ? 'Marcare Sosit' : 'Marcare Plecat'}
                        </Button>
                        <Button
                            size="sm"
                            variant="danger"
                            onClick={() => handleDemisie(driver)}
                        >
                            Demisie
                        </Button>
                    </div>
                )}
            />

            <DriverFormModal
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                driver={selectedDriver}
                onSubmit={handleSubmit}
            />

            <DemisieModal
                isOpen={isDemisieModalOpen}
                onClose={() => {
                    setIsDemisieModalOpen(false);
                    setDriverForDemisie(null);
                }}
                driver={driverForDemisie}
                onSubmit={handleDemisieSubmit}
            />
        </div>
    );
};

export default DriverList;
