import React, { useState, useEffect } from 'react';
import { driversAPI, salariesAPI, finesAPI } from '../../services/api';
import { formatDate, calculateDays } from '../../utils/dateUtils';
import FormInput from '../shared/forms/FormInput';
import Select from '../shared/forms/Select';
import Button from '../shared/buttons/Button';
import DeducereModal from './modals/DeducereModal';
import AvansModal from './modals/AvansModal';

const SalaryCalculator = () => {
    const [drivers, setDrivers] = useState([]);
    const [selectedDriver, setSelectedDriver] = useState('');
    const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth() + 1);
    const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());

    const [calculSalariu, setCalculSalariu] = useState({
        salariuBaza: 0,
        diurna: {
            zileInAfara: 0,
            sumaTotala: 0,
            perioade: []
        },
        deduceri: [],
        avansuri: [],
        totalDeduceri: 0,
        totalAvansuri: 0,
        totalDePlata: 0
    });

    const [driverDetails, setDriverDetails] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [salary, setSalary] = useState(null);
    const [isDeducereModalOpen, setIsDeducereModalOpen] = useState(false);
    const [isAvansModalOpen, setIsAvansModalOpen] = useState(false);

    useEffect(() => {
        loadDrivers();
    }, []);

    const loadDrivers = async () => {
        try {
            const response = await driversAPI.getAll();
            setDrivers(response.data.map(driver => ({
                value: driver._id,
                label: driver.nume
            })));
        } catch (error) {
            console.error('Error loading drivers:', error);
            setError('Nu s-au putut încărca șoferii');
        }
    };

    const loadSalaryData = async () => {
        if (!selectedDriver) return;

        try {
            setLoading(true);
            setError(null);
            
            // Încarcă detaliile șoferului
            const driverResponse = await driversAPI.getById(selectedDriver);
            const driver = driverResponse.data;
            setDriverDetails(driver);

            // Calculează perioada efectivă
            const perioadaStart = new Date(selectedYear, selectedMonth - 1, 1);
            const perioadaEnd = new Date(selectedYear, selectedMonth, 0);

            // Ajustează perioada dacă șoferul este inactiv
            let dataCalculEfectiv = perioadaEnd;
            if (!driver.activ && driver.dataIncheiereContract) {
                if (new Date(driver.dataIncheiereContract) <= perioadaEnd) {
                    dataCalculEfectiv = new Date(driver.dataIncheiereContract);
                }
            }

            // Încarcă perioadele pentru diurnă
            const perioadePlecari = await driversAPI.getPerioade(selectedDriver, {
                dataStart: perioadaStart.toISOString(),
                dataEnd: dataCalculEfectiv.toISOString()
            });

            // Filtrează și ajustează perioadele pentru diurnă
            const perioadeAjustate = perioadePlecari.data.map(perioada => {
                let dataFinala = new Date(perioada.dataFinal);
                if (driver.dataUltimaSosire && dataFinala > new Date(driver.dataUltimaSosire)) {
                    dataFinala = new Date(driver.dataUltimaSosire);
                }
                return {
                    ...perioada,
                    dataFinal: dataFinala
                };
            });

            // Încarcă salariul existent sau creează unul nou
            const salariesResponse = await salariesAPI.getByDriver(selectedDriver);
            let existingSalary = salariesResponse.data.find(s => 
                new Date(s.luna).getMonth() === perioadaStart.getMonth() &&
                new Date(s.luna).getFullYear() === perioadaStart.getFullYear()
            );

            if (!existingSalary) {
                const newSalaryData = {
                    sofer: selectedDriver,
                    luna: perioadaStart,
                    salariuBaza: driver.salariuBaza || 0,
                    zileLucrate: 0,
                    status: 'Draft'
                };
                const newSalaryResponse = await salariesAPI.create(newSalaryData);
                existingSalary = newSalaryResponse.data;
            }
            setSalary(existingSalary);

            // Încarcă amenzile active
            const finesResponse = await finesAPI.getByDriver(selectedDriver);
            const activeDeductions = finesResponse.data
                .filter(fine => 
                    new Date(fine.dataAmenda).getMonth() === perioadaStart.getMonth() &&
                    new Date(fine.dataAmenda).getFullYear() === perioadaStart.getFullYear() &&
                    fine.statusPlata !== 'Platit'
                )
                .map(fine => ({
                    tip: 'Amendă',
                    suma: fine.sumaTotala,
                    descriere: fine.tipAmenda
                }));

            // Calculează zilele de diurnă
            const zileInAfara = perioadeAjustate.reduce((total, perioada) => {
                if (perioada.status === 'Plecat') {
                    return total + calculateDays(perioada.dataStart, perioada.dataFinal);
                }
                return total;
            }, 0);

            // Calculează diurna (70 EUR pe zi)
            const sumaDiurna = zileInAfara * 70;

            setCalculSalariu({
                salariuBaza: existingSalary.salariuBaza,
                diurna: {
                    zileInAfara,
                    sumaTotala: sumaDiurna,
                    perioade: perioadeAjustate.filter(p => p.status === 'Plecat')
                },
                deduceri: [...existingSalary.deduceri || [], ...activeDeductions],
                avansuri: existingSalary.bonusuri?.filter(b => b.tip === 'Avans') || [],
                totalDeduceri: [...(existingSalary.deduceri || []), ...activeDeductions]
                    .reduce((sum, d) => sum + d.suma, 0),
                totalAvansuri: (existingSalary.bonusuri || [])
                    .filter(b => b.tip === 'Avans')
                    .reduce((sum, a) => sum + a.suma, 0),
                totalDePlata: existingSalary.salariuBaza - 
                            [...(existingSalary.deduceri || []), ...activeDeductions]
                                .reduce((sum, d) => sum + d.suma, 0) -
                            (existingSalary.bonusuri || [])
                                .filter(b => b.tip === 'Avans')
                                .reduce((sum, a) => sum + a.suma, 0)
            });

            // Actualizează salariul cu diurna calculată
            if (existingSalary.diurna !== sumaDiurna || existingSalary.zileLucrate !== zileInAfara) {
                await salariesAPI.update(existingSalary._id, {
                    diurna: sumaDiurna,
                    zileLucrate: zileInAfara
                });
            }

        } catch (error) {
            console.error('Error loading salary data:', error);
            setError('Eroare la încărcarea datelor salariale');
        } finally {
            setLoading(false);
        }
    };

    const handleAddDeducere = async (deducereData) => {
        if (!salary) return;
        try {
            await salariesAPI.addDeducere(salary._id, deducereData);
            await loadSalaryData(); // Reîncarcă datele
        } catch (error) {
            console.error('Error adding deducere:', error);
            setError('Eroare la adăugarea deducerii');
        }
    };

    const handleAddAvans = async (avansData) => {
        if (!salary) return;
        try {
            await salariesAPI.addBonus(salary._id, {
                ...avansData,
                tip: 'Avans'
            });
            await loadSalaryData(); // Reîncarcă datele
        } catch (error) {
            console.error('Error adding avans:', error);
            setError('Eroare la adăugarea avansului');
        }
    };

    const handleCalculateFinal = async () => {
        if (!salary) return;
        try {
            await salariesAPI.calculate(salary._id);
            await loadSalaryData(); // Reîncarcă datele
        } catch (error) {
            console.error('Error calculating salary:', error);
            setError('Eroare la calculul final al salariului');
        }
    };

    return (
        <div className="space-y-6">
            {/* Controale selecție */}
            <div className="bg-white p-4 rounded-lg shadow">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <Select
                        label="Șofer"
                        name="driver"
                        value={selectedDriver}
                        onChange={(e) => setSelectedDriver(e.target.value)}
                        options={drivers}
                    />
                    
                    <div className="grid grid-cols-2 gap-2">
                        <Select
                            label="Luna"
                            name="month"
                            value={selectedMonth.toString()}
                            onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
                            options={[
                                {value: "1", label: "Ianuarie"},
                                {value: "2", label: "Februarie"},
                                {value: "3", label: "Martie"},
                                {value: "4", label: "Aprilie"},
                                {value: "5", label: "Mai"},
                                {value: "6", label: "Iunie"},
                                {value: "7", label: "Iulie"},
                                {value: "8", label: "August"},
                                {value: "9", label: "Septembrie"},
                                {value: "10", label: "Octombrie"},
                                {value: "11", label: "Noiembrie"},
                                {value: "12", label: "Decembrie"}
                            ]}
                        />
                        
                        <FormInput
                            type="number"
                            label="An"
                            value={selectedYear}
                            onChange={(e) => setSelectedYear(parseInt(e.target.value))}
                            min={2000}
                            max={2100}
                        />
                    </div>

                    <div className="flex items-end">
                        <Button 
                            onClick={loadSalaryData} 
                            disabled={!selectedDriver}
                            variant="primary"
                        >
                            Calculează
                        </Button>
                    </div>
                </div>
            </div>

            {loading ? (
                <div>Se încarcă...</div>
            ) : error ? (
                <div className="text-red-600">{error}</div>
            ) : driverDetails && (
                <>
                    {/* Status șofer inactiv */}
                    {!driverDetails.activ && (
                        <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4">
                            <p className="text-yellow-700">
                                Șofer inactiv - Calcul până la data: {formatDate(driverDetails.dataIncheiereContract)}
                            </p>
                        </div>
                    )}

                    {/* Sumar Salariu */}
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                        <div className="bg-white p-4 rounded-lg shadow">
                            <p className="text-sm text-gray-500">Salariu Bază (RON)</p>
                            <p className="text-2xl font-bold">{calculSalariu.salariuBaza}</p>
                        </div>
                        <div className="bg-white p-4 rounded-lg shadow">
                            <p className="text-sm text-gray-500">Diurnă Total (EUR)</p>
                            <p className="text-2xl font-bold">{calculSalariu.diurna.sumaTotala}</p>
                            <p className="text-sm text-gray-500">
                                ({calculSalariu.diurna.zileInAfara} zile x 70 EUR)
                            </p>
                        </div>
                        <div className="bg-white p-4 rounded-lg shadow">
                            <p className="text-sm text-gray-500">Total de Plată</p>
                            <p className="text-2xl font-bold text-green-600">
                                {calculSalariu.totalDePlata} RON
                                {' + '}
                                {calculSalariu.diurna.sumaTotala} EUR
                            </p>
                        </div>
                    </div>

                    {/* Perioade Diurnă */}
                    <div className="bg-white p-6 rounded-lg shadow">
                        <h3 className="text-lg font-medium mb-4">Perioade Diurnă</h3>
                        <div className="space-y-2">
                            {calculSalariu.diurna.perioade.map((perioada, index) => (
                                <div key={index} className="flex justify-between border-b py-2">
                                    <div>
                                        <p className="font-medium">
                                            {formatDate(perioada.dataStart)} - {formatDate(perioada.dataFinal)}
                                        </p>
                                        <p className="text-sm text-gray-500">
                                            {calculateDays(perioada.dataStart, perioada.dataFinal)} zile
                                        </p>
                                    </div>
                                    <p className="font-medium">
                                        {calculateDays(perioada.dataStart, perioada.dataFinal) * 70} EUR
                                    </p>
                                </div>
                            ))}
                        </div>
                    </div>

                    {/* Deduceri și Avansuri */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="bg-white p-6 rounded-lg shadow">
                            <h3 className="text-lg font-medium mb-4">Deduceri</h3>
                            <div className="space-y-2">
                                {calculSalariu.deduceri.map((deducere, index) => (
                                    <div key={index} className="flex justify-between border-b py-2">
                                        <div>
                                            <p className="font-medium">{deducere.tip}</p>
                                            <p className="text-sm text-gray-500">
                                                {deducere.descriere}
                                            </p>
                                        </div><p className="font-medium text-red-600">
                                            -{deducere.suma} RON
                                        </p>
                                    </div>
                                ))}
                                <div className="mt-4 pt-2 border-t">
                                    <div className="flex justify-between">
                                        <p className="font-medium">Total Deduceri</p>
                                        <p className="font-medium text-red-600">
                                            -{calculSalariu.totalDeduceri} RON
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="bg-white p-6 rounded-lg shadow">
                            <h3 className="text-lg font-medium mb-4">Avansuri</h3>
                            <div className="space-y-2">
                                {calculSalariu.avansuri.map((avans, index) => (
                                    <div key={index} className="flex justify-between border-b py-2">
                                        <div>
                                            <p className="font-medium">Avans {formatDate(avans.data)}</p>
                                            <p className="text-sm text-gray-500">
                                                {avans.descriere}
                                            </p>
                                        </div>
                                        <p className="font-medium text-blue-600">
                                            -{avans.suma} RON
                                        </p>
                                    </div>
                                ))}
                                <div className="mt-4 pt-2 border-t">
                                    <div className="flex justify-between">
                                        <p className="font-medium">Total Avansuri</p>
                                        <p className="font-medium text-blue-600">
                                            -{calculSalariu.totalAvansuri} RON
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Butoane acțiuni */}
                    <div className="flex justify-end space-x-4">
                        <Button
                            onClick={() => setIsDeducereModalOpen(true)}
                            variant="secondary"
                            disabled={!salary || salary.status === 'Calculat'}
                        >
                            Adaugă Deducere
                        </Button>
                        <Button
                            onClick={() => setIsAvansModalOpen(true)}
                            variant="secondary"
                            disabled={!salary || salary.status === 'Calculat'}
                        >
                            Adaugă Avans
                        </Button>
                        <Button
                            onClick={handleCalculateFinal}
                            variant="primary"
                            disabled={!salary || salary.status === 'Calculat'}
                        >
                            Finalizează Calcul
                        </Button>
                    </div>

                    {/* Modale */}
                    <DeducereModal
                        isOpen={isDeducereModalOpen}
                        onClose={() => setIsDeducereModalOpen(false)}
                        onSubmit={handleAddDeducere}
                    />
                    
                    <AvansModal
                        isOpen={isAvansModalOpen}
                        onClose={() => setIsAvansModalOpen(false)}
                        onSubmit={handleAddAvans}
                    />
                </>
            )}
        </div>
    );
};

export default SalaryCalculator;
