// frontend/src/components/dashboard/DriverStatus.js
import React, { useState, useEffect } from 'react';
import { formatDate } from '../../utils/dateUtils';
import api from '../../services/api';

const DriverStatus = () => {
  const [drivers, setDrivers] = useState([]);

  useEffect(() => {
      loadDrivers();
  }, []);

  const loadDrivers = async () => {
      try {
          const response = await api.get('/drivers', {
              params: { status: 'active' }
          });
          setDrivers(response.data);
      } catch (error) {
          console.error('Error loading drivers:', error);
      }
  };

  return (
      <div className="bg-white rounded-lg shadow">
          <div className="p-4 border-b">
              <h2 className="text-lg font-medium">Status Șoferi</h2>
          </div>
          <div className="p-4">
              <div className="grid grid-cols-1 gap-4">
                  {drivers.map(driver => (
                      <DriverCard key={driver._id} driver={driver} />
                  ))}
              </div>
          </div>
      </div>
  );
};

const DriverCard = ({ driver }) => (
  <div className="border rounded-lg p-4">
      <div className="flex justify-between items-start">
          <div>
              <h3 className="font-medium">{driver.nume}</h3>
              <p className="text-sm text-gray-500">
                  {driver.vehiculAsignat?.numarInmatriculare || 'Fără vehicul'}
              </p>
          </div>
          <StatusBadge status={driver.status} />
      </div>
  </div>
);

const StatusBadge = ({ status }) => {
  const colors = {
      'Activ': 'bg-green-100 text-green-800',
      'In Cursa': 'bg-blue-100 text-blue-800',
      'Concediu': 'bg-yellow-100 text-yellow-800',
      'Suspendat': 'bg-red-100 text-red-800'
  };

  return (
      <span className={`px-2 py-1 rounded-full text-xs ${colors[status] || ''}`}>
          {status}
      </span>
  );
};

export default DriverStatus;
