// C:\Users\Administrator\OneDrive\Documente\GitHub\salarizare\modern-backend\frontend\src\components\salaries\SalaryCalculator.js

import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import { formatDate, calculateDays } from '../../utils/dateUtils';
import FormInput from '../shared/forms/FormInput';
import Select from '../shared/forms/Select';
import Button from '../shared/buttons/Button';

const SalaryCalculator = ({ driverId, month, year }) => {
    const [calculSalariu, setCalculSalariu] = useState({
        salariuBaza: 0,
        diurna: {
            zileInAfara: 0,
            sumaTotala: 0,
            perioade: []
        },
        deduceri: [],
        avansuri: [],
        totalDeduceri: 0,
        totalAvansuri: 0,
        totalDePlata: 0
    });

    const [driverDetails, setDriverDetails] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (driverId && month && year) {
            loadSalaryData();
        }
    }, [driverId, month, year]);

    const loadSalaryData = async () => {
        try {
            setLoading(true);
            
            // Încarcă detaliile șoferului
            const driverResponse = await api.get(`/drivers/${driverId}`);
            const driver = driverResponse.data;
            setDriverDetails(driver);

            // Calculează perioada efectivă
            const perioadaStart = new Date(year, month - 1, 1);
            const perioadaEnd = new Date(year, month, 0);

            // Ajustează perioada dacă șoferul este inactiv
            let dataCalculEfectiv = perioadaEnd;
            if (!driver.activ && driver.dataIncheiereContract) {
                if (new Date(driver.dataIncheiereContract) <= perioadaEnd) {
                    dataCalculEfectiv = new Date(driver.dataIncheiereContract);
                }
            }

            // Încarcă perioadele pentru diurnă
            const perioadePlecari = await api.get(`/drivers/${driverId}/perioade`, {
                params: {
                    dataStart: perioadaStart.toISOString(),
                    dataEnd: dataCalculEfectiv.toISOString()
                }
            });

            // Filtrează și ajustează perioadele pentru diurnă
            const perioadeAjustate = perioadePlecari.data.map(perioada => {
                let dataFinala = new Date(perioada.dataFinal);
                if (driver.dataUltimaSosire && dataFinala > new Date(driver.dataUltimaSosire)) {
                    dataFinala = new Date(driver.dataUltimaSosire);
                }
                return {
                    ...perioada,
                    dataFinal: dataFinala
                };
            });

            // Calculează zilele de diurnă
            const zileInAfara = perioadeAjustate.reduce((total, perioada) => {
                if (perioada.status === 'Plecat') {
                    return total + calculateDays(perioada.dataStart, perioada.dataFinal);
                }
                return total;
            }, 0);

            // Calculează diurna (70 EUR pe zi)
            const sumaDiurna = zileInAfara * 70;

            // Încarcă detaliile salariului
            const salariuResponse = await api.get(`/drivers/${driverId}/salariu`, {
                params: { month, year }
            });
            const salariuDetails = salariuResponse.data;

            setCalculSalariu({
                salariuBaza: salariuDetails.salariuBaza,
                diurna: {
                    zileInAfara,
                    sumaTotala: sumaDiurna,
                    perioade: perioadeAjustate.filter(p => p.status === 'Plecat')
                },
                deduceri: salariuDetails.deduceri || [],
                avansuri: salariuDetails.avansuri || [],
                totalDeduceri: (salariuDetails.deduceri || []).reduce((sum, d) => sum + d.suma, 0),
                totalAvansuri: (salariuDetails.avansuri || []).reduce((sum, a) => sum + a.suma, 0),
                totalDePlata: salariuDetails.salariuBaza - 
                            (salariuDetails.deduceri || []).reduce((sum, d) => sum + d.suma, 0) -
                            (salariuDetails.avansuri || []).reduce((sum, a) => sum + a.suma, 0)
            });

        } catch (error) {
            console.error('Error loading salary data:', error);
            setError('Eroare la încărcarea datelor salariale');
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <div>Se încarcă...</div>;
    }

    if (error) {
        return <div className="text-red-600">{error}</div>;
    }

    return (
        <div className="space-y-6">
            {/* Status șofer */}
            {driverDetails && !driverDetails.activ && (
                <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4">
                    <p className="text-yellow-700">
                        Șofer inactiv - Calcul până la data: {formatDate(driverDetails.dataIncheiereContract)}
                    </p>
                </div>
            )}

            {/* Sumar Salariu */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="bg-white p-4 rounded-lg shadow">
                    <p className="text-sm text-gray-500">Salariu Bază (RON)</p>
                    <p className="text-2xl font-bold">{calculSalariu.salariuBaza}</p>
                </div>
                <div className="bg-white p-4 rounded-lg shadow">
                    <p className="text-sm text-gray-500">Diurnă Total (EUR)</p>
                    <p className="text-2xl font-bold">{calculSalariu.diurna.sumaTotala}</p>
                    <p className="text-sm text-gray-500">
                        ({calculSalariu.diurna.zileInAfara} zile x 70 EUR)
                    </p>
                </div>
                <div className="bg-white p-4 rounded-lg shadow">
                    <p className="text-sm text-gray-500">Total de Plată</p>
                    <p className="text-2xl font-bold text-green-600">
                        {calculSalariu.totalDePlata} RON
                        {' + '}
                        {calculSalariu.diurna.sumaTotala} EUR
                    </p>
                </div>
            </div>

            {/* Perioade Diurnă */}
            <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-lg font-medium mb-4">Perioade Diurnă</h3>
                <div className="space-y-2">
                    {calculSalariu.diurna.perioade.map((perioada, index) => (
                        <div key={index} className="flex justify-between border-b py-2">
                            <div>
                                <p className="font-medium">
                                    {formatDate(perioada.dataStart)} - {formatDate(perioada.dataFinal)}
                                </p>
                                <p className="text-sm text-gray-500">
                                    {calculateDays(perioada.dataStart, perioada.dataFinal)} zile
                                </p>
                            </div>
                            <p className="font-medium">
                                {calculateDays(perioada.dataStart, perioada.dataFinal) * 70} EUR
                            </p>
                        </div>
                    ))}
                </div>
            </div>

            {/* Deduceri și Avansuri */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="bg-white p-6 rounded-lg shadow">
                    <h3 className="text-lg font-medium mb-4">Deduceri</h3>
                    <div className="space-y-2">
                        {calculSalariu.deduceri.map((deducere, index) => (
                            <div key={index} className="flex justify-between border-b py-2">
                                <div>
                                    <p className="font-medium">{deducere.motiv}</p>
                                    <p className="text-sm text-gray-500">
                                        {formatDate(deducere.data)}
                                    </p>
                                </div>
                                <p className="font-medium text-red-600">
                                    -{deducere.suma} RON
                                </p>
                            </div>
                        ))}
                        <div className="mt-4 pt-2 border-t">
                            <div className="flex justify-between">
                                <p className="font-medium">Total Deduceri</p>
                                <p className="font-medium text-red-600">
                                    -{calculSalariu.totalDeduceri} RON
                                </p>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="bg-white p-6 rounded-lg shadow">
                    <h3 className="text-lg font-medium mb-4">Avansuri</h3>
                    <div className="space-y-2">
                        {calculSalariu.avansuri.map((avans, index) => (
                            <div key={index} className="flex justify-between border-b py-2">
                                <div>
                                    <p className="font-medium">Avans {formatDate(avans.data)}</p>
                                </div>
                                <p className="font-medium text-blue-600">
                                    -{avans.suma} RON
                                </p>
                            </div>
                        ))}
                        <div className="mt-4 pt-2 border-t">
                            <div className="flex justify-between">
                                <p className="font-medium">Total Avansuri</p>
                                <p className="font-medium text-blue-600">
                                    -{calculSalariu.totalAvansuri} RON
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default SalaryCalculator;
