// C:\Users\Administrator\OneDrive\Documente\GitHub\salarizare\modern-backend\frontend\src\components\dashboard\ActivityFeed.js
import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import { formatDate } from '../../utils/dateUtils';

const ActivityFeed = () => {
   const [activities, setActivities] = useState([]);

   useEffect(() => {
       loadActivities();
   }, []);

   const loadActivities = async () => {
       const response = await api.get('/history', {
           params: { limit: 10 }
       });
       setActivities(response.data);
   };

   return (
       <div className="bg-white rounded-lg shadow p-6">
           <h2 className="text-lg font-medium mb-4">Activitate RecentÄƒ</h2>
           <div className="space-y-4">
               {activities.map(activity => (
                   <div
                       key={activity._id}
                       className="flex items-start space-x-3 pb-4 border-b"
                   >
                       <div className="flex-1">
                           <p className="text-sm">{activity.description}</p>
                           <p className="text-xs text-gray-500">
                               {formatDate(activity.timestamp)}
                           </p>
                       </div>
                   </div>
               ))}
           </div>
       </div>
   );
};

export default ActivityFeed;
